import express from 'express';
import cors from 'cors';
import os from 'os';
import authRoutes from './routes/AuthRoutes';
import userRoutes from './routes/UserRoutes';
import dotenv from 'dotenv';

dotenv.config();

const app = express();

app.use(cors());
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.get('/api/getUsername', (req, res) => res.send({ username: os.userInfo().username }));

app.use('/api/auth', authRoutes);
app.use('/api/user', userRoutes);

app.listen(process.env.PORT || 8080, () => console.log(`Listening on port ${process.env.PORT || 8080}!`));

export default app;

import { Router } from 'express';
import AuthController from '../controllers/AuthController';
import auth from '../util/auth';

const router = Router();

router.post('/signup', AuthController.signup);
router.post('/signin', AuthController.signin);

router.post('/password', auth, AuthController.passwordChange);

router.post('/forgot', AuthController.forgot);

router.get('/reset/:token', AuthController.validateResetToken);
router.post('/reset/:token', AuthController.reset);

export default router;

import jwt from 'jsonwebtoken';
import validator from 'validator';
import crypto from 'crypto';
import UserService from '../services/UserService';
import Util from '../util/Utils';

const util = new Util();

class AuthController {
  static async signup(req, res) {
    const { email, password, firstName, lastName } = req.body;
    if ( !validator.isEmail(email) || !String(password) || !String(firstName)|| !String(lastName)) {
      util.setError(400, 'Please input a valid value');
      return util.send(res);
    }

    try {
      const user = await UserService.register(req.body);
      user.password = undefined;
      const token = jwt.sign({id: user.id, email: user.email}, process.env.JWT_KEY, { expiresIn: 36000 });
      util.setSuccess(201, 'User is successfully signed up', {
        user: user,
        token: token
      })
      return util.send(res);
    } catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }

  static async signin(req, res) {
    try {
      const { email, password } = req.body;
      const user = await UserService.getUser(email);
      if (!user) {
        throw 'Login failed! Check authentication credentials';
      }

      const isPasswordMatch = await UserService.validatePassword(password, user.password)
      if (!isPasswordMatch) {
        throw 'Login failed! Check authentication credentials';
      }
      user.password = undefined;

      const token = jwt.sign({id: user.id, email: user.email}, process.env.JWT_KEY, { expiresIn: 36000 });
      util.setSuccess(200, 'User is successfully signed in', {
        user: user,
        token: token
      })

      return util.send(res);
    } catch(error) {
      util.setError(401, error);
      return util.send(res);
    }
  }

  static async passwordChange(req, res) {
    try {
      const { email, currentPassword, newPassword, verifyPassword } = req.body;

      const user = await UserService.getUser(email);
      if (!user) {
        throw 'No account with that email has been found';
      }

      const isPasswordMatch = await UserService.validatePassword(currentPassword, user.password)
      if (!isPasswordMatch) {
        throw {
          statusCode: 401,
          message: 'Current password is incorrect.'
        }
      }

      if (newPassword === verifyPassword) {
        user.password = newPassword;
        await UserService.updateUser(user);

        // send password updated email

        util.setSuccess(200, 'Password reset email has been sent.')
        return util.send(res);
      } else {
        throw 'Passwords do not match';
      }
    } catch(error) {
      if (error.statusCode) {
        util.setError(error.statusCode, error.message);
      } else {
        util.setError(400, error);
      }
      return util.send(res);
    }
  }


  static async forgot(req, res) {
    try {
      const { email } = req.body;
      const user = await UserService.getUser(email);
      if (!user) {
        throw 'No account with that email has been found';
      }

      //generating random reset token
      const token = await new Promise((res, rej) => {
        crypto.randomBytes(10, (err, buffer) => {
          if (err) {
            rej(err);
          }
          res(buffer.toString("hex"));
        });
      });

      user.resetPasswordToken = token;
      user.resetPasswordExpires = new Date(Date.now() + 3600000); // 1 hour

      await UserService.updateUserResetToken(user);

      // send email

      util.setSuccess(200, 'Password reset email has been sent.')
      return util.send(res);
    } catch(error) {
      util.setError(400, error);
      return util.send(res);
    }
  }

  static async validateResetToken(req, res) {
    try {
      const { token } = req.params;
      const user = await UserService.getResetUser(token, new Date());
      if (!user) {
        throw 'Password reset token is invalid or has expired.';
      }

      util.setSuccess(200, 'Token was found successfully.');
      return util.send(res);
    } catch(error) {
      util.setError(400, error);
      return util.send(res);
    }
  }

  static async reset(req, res) {
    try {
      const { newPassword, verifyPassword } = req.body;
      const user = await UserService.getResetUser(req.params.token, new Date());
      if (!user) {
        throw 'Password reset token is invalid or has expired.';
      }

      if (newPassword === verifyPassword) {
        user.password = newPassword;
        user.resetPasswordToken = undefined;
        user.resetPasswordExpires = undefined // 1 hour

        await UserService.updateUser(user);

        // send password updated email

        util.setSuccess(200, 'Password reset email has been sent.')
        return util.send(res);
      } else {
        throw 'Passwords do not match';
      }
    } catch(error) {
      util.setError(400, error);
      return util.send(res);
    }
  }
}

export default AuthController;

import makeDb from '../util/database';
import User from '../models/User';
import bcrypt from 'bcrypt';

class UserService {
  static async register(user) {
    const db = makeDb();
    try {
      const passwordHash = await bcrypt.hash(user.password, 10);
      const date = new Date();
      const records = [
        [user.email, passwordHash, user.firstName, user.lastName, date, date]
      ]

      await db.beginTransaction();
      const users = await db.query('SELECT * FROM `user` WHERE `email` = ?',[user.email]);
      if (users.length > 0) {
        throw 'User already exists!!';
      }

      const result = await db.query(
        'INSERT INTO  `user` (email, password, first_name, last_name, updated, created) VALUES ?'
        , [records]);
      user.id = result.insertId;

      const roleRecords = [
        [user.id, 2]
      ];
      await db.query(
        'INSERT INTO  `role` (user_id, role_type_id) VALUES ?'
        , [roleRecords]);

      await db.commit();
      return new User(user.id, user.email, undefined, user.firstName, user.lastName, ['user'], undefined, undefined, date, date);
    } catch (error) {
      await db.rollback();
      throw error;
    } finally {
      await db.close();
    }
  }

  static async getUser(email) {
    const db = makeDb();
    try {
      const users = await db.query('SELECT * FROM `user` WHERE `email` = ?',[email]);
      if (users.length > 0) {
        // retrieving roles by userid
        const roles = await db.query(
          'SELECT rt.name AS name FROM `user` u ' +
          'JOIN `role` r ON u.id = r.user_id ' +
          'JOIN `role_type` rt on r.role_type_id = rt.id ' +
          'WHERE `user_id` = ?', [users[0].id]);
        let userRoles = [];
        if (roles) {
          for(const role of roles) {
            userRoles.push(role.name);
          }
        }
        return new User(users[0].id, users[0].email, users[0].password, users[0].first_name, users[0].last_name, userRoles, users[0].reset_password_token, users[0].reset_password_expires, users[0].updated, users[0].created);
      } else {
        return undefined;
      }
    } catch (error) {
      throw error;
    } finally {
      await db.close();
    }
  }

  static async getResetUser(token, date) {
    const db = makeDb();
    try {
      const users = await db.query('SELECT * FROM `user` WHERE `reset_password_token` = ? AND `reset_password_expires` > ?',[token, date]);
      if (users.length > 0) {
        // retrieving roles by userid
        const roles = await db.query(
          'SELECT rt.name AS name FROM `user` u ' +
          'JOIN `role` r ON u.id = r.user_id ' +
          'JOIN `role_type` rt on r.role_type_id = rt.id ' +
          'WHERE `user_id` = ?', [users[0].id]);
        let userRoles = [];
        if (roles) {
          for(const role of roles) {
            userRoles.push(role.name);
          }
        }
        return new User(users[0].id, users[0].email, undefined, users[0].first_name, users[0].last_name, userRoles, users[0].updated, users[0].created);
      } else {
        return undefined;
      }
    } catch (error) {
      throw error;
    } finally {
      await db.close();
    }
  }

  static async updateUser(user) {
    const db = makeDb();
    try {
      const passwordHash = await bcrypt.hash(user.password, 10);
      await db.query(
        'UPDATE `user` SET ' +
        '`first_name` = ?, ' +
        '`last_name` = ?, ' +
        '`password` = ?, ' +
        '`reset_password_token` = ?, ' +
        '`reset_password_expires` = ?, ' +
        '`updated` = ? ' +
        'WHERE `email` = ?'
        , [user.firstName, user.lastName, passwordHash, user.resetPasswordToken, user.resetPasswordExpires, new Date(), user.email]);
    } catch (error) {
      throw error;
    } finally {
      await db.close();
    }
  }

  static async updateUserResetToken(user) {
    const db = makeDb();
    try {
      const passwordHash = await bcrypt.hash(user.password, 10);
      await db.query(
        'UPDATE `user` SET ' +
        '`reset_password_token` = ?, ' +
        '`reset_password_expires` = ?, ' +
        '`updated` = ? ' +
        'WHERE `email` = ?'
        , [user.resetPasswordToken, user.resetPasswordExpires, new Date(), user.email]);
    } catch (error) {
      throw error;
    } finally {
      await db.close();
    }
  }

  static async validatePassword(password, passwordHash) {
    return await bcrypt.compare(password, passwordHash);
  }
}

export default UserService;

import mysql from 'mysql';
import util from 'util';
import dbConfig from '../config/db.config';

const env = process.env.NODE_ENV ? process.env.NODE_ENV : 'development';
const config = dbConfig[env];

function makeDb() {
  const connection = mysql.createConnection( config );
  return {
    query( sql, args ) {
      return util.promisify( connection.query )
        .call( connection, sql, args );
    },
    close() {
      return util.promisify( connection.end ).call( connection );
    },
    beginTransaction() {
      return util.promisify( connection.beginTransaction )
        .call( connection );
    },
    commit() {
      return util.promisify( connection.commit )
        .call( connection );
    },
    rollback() {
      return util.promisify( connection.rollback )
        .call( connection );
    }
  };
}

export default makeDb;

