export default class User {
  constructor(id, email, password, firstName, lastName, roles, resetPasswordToken, resetPasswordExpires, updated, created) {
    this.id = id;
    this.email = email;
    this.password = password;
    this.firstName = firstName;
    this.lastName = lastName;
    this.roles = roles;
    this.resetPasswordToken = resetPasswordToken;
    this.resetPasswordExpires = resetPasswordExpires;
    this.updated = updated;
    this.created = created;
  }
}

export default class Util {
    constructor() {
      this.statusCode = null;
      this.type = null;
      this.data = null;
      this.message = null;
    }

    setSuccess(statusCode, message, data) {
      this.statusCode = statusCode;
      this.message = message;
      this.data = data;
      this.type = 'success';
    }

    setError(statusCode, message) {
      this.statusCode = statusCode;
      this.message = message;
      this.type = 'error';
    }

    send(res) {
      const result = {
        status: this.type,
        message: this.message,
        data: this.data,
      };

      console.log(result);
      if (this.type === 'success') {
        return res.status(this.statusCode).json(result);
      }
      return res.status(this.statusCode).json({
        status: this.type,
        message: this.message,
      });
    }
  }

import jwt from 'jsonwebtoken';
import UserService from '../services/UserService';
import Util from './Utils';

const util = new Util();

const auth = async(req, res, next) => {
    const errorMsg = 'Not authorized to access this resource.';
    const token = req.header('Authorization').replace('Bearer ', '');
    const email = req.method === 'POST' ? req.body.email : req.params.email;
    try {
        const data = jwt.verify(token, process.env.JWT_KEY);
        //current logged in user
        const user = await UserService.getUser(data.email);
        if (!user) {
            throw errorMsg;
        }

         //authorize
        if (email != user.email && !user.roles.includes('admin')) {
            throw errorMsg;
        }

        req.user = user;
        req.token = token;
        next()
    } catch (error) {
        util.setError(403, error);
        return util.send(res);
    }
}

export default auth;

